import { json, redirect, useRouteLoaderData } from 'react-router-dom';

import EventItem from '../components/EventItem';
import EventsList from '../components/EventsList';

const EventDetailPage = () => {
   const data = useRouteLoaderData('event-detail');

   return (
      <>
         <EventItem event={data.event} />;
         <EventsList />
      </>
   );
};

export default EventDetailPage;

export async function loader({ request, params }) {
   const eventId = params.eventID;

   const response = await fetch('http://localhost:8080/events/' + eventId);

   if (!response.ok) {
      throw json({ message: 'Could not found details for selected events.' }, { status: 500 });
   } else {
      return response;
   }
}

export async function action({ params, request }) {
   const eventId = params.eventID;
   const response = await fetch('http://localhost:8080/events/' + eventId, { method: request.method });

   if (!response.ok) {
      throw json({ message: 'Cound not delete event.' }, { status: 500 });
   }

   return redirect('/events');
}
