import { useRouteError } from 'react-router-dom';

import PageContent from '../components/PageContent';

function ErrorPage() {
   const error = useRouteError();

   let title = 'An error accured';
   let message = 'Somethng went wrong!';

   if (error.status === 500) {
      message = error.data.message;
   }

   if (error.status === 404) {
      title = 'Not found!';
      message = 'Could not found resorce or page';
   }

   return (
      <PageContent title="An error accured">
         <p>{message}</p>
      </PageContent>
   );
}

export default ErrorPage;
