import { Suspense } from 'react';
import { json, redirect, useRouteLoaderData, defer, Await } from 'react-router-dom';

import EventItem from '../components/EventItem';
import EventsList from '../components/EventsList';

const EventDetailPage = () => {
   const { event, events } = useRouteLoaderData('event-detail');

   return (
      <>
         <Suspense fallback={<p style={{ textAlign: 'center' }}>Loading...</p>}>
            <Await resolve={event}>{(loadedEvent) => <EventItem event={loadedEvent} />};</Await>
         </Suspense>
         <Suspense>
            <Await resolve={events}>{(loadedEvents) => <EventsList events={loadedEvents} />}</Await>
         </Suspense>
      </>
   );
};

export default EventDetailPage;

async function loadEvent(eventId) {
   const response = await fetch('http://localhost:8080/events/' + eventId);

   if (!response.ok) {
      throw json({ message: 'Could not found details for selected events.' }, { status: 500 });
   } else {
      const resData = await response.json();
      return resData.event;
   }
}

async function loadEvents() {
   const response = await fetch('http://localhost:8080/events');

   if (!response.ok) {
      // return { isError: true, message: 'Could not fetch events ' };
      // throw new Response(JSON.stringify({ message: 'Could nod fetch events' }), { status: 500 });
      return json({ message: 'Could nod fetch events' }, { status: 500 });
   } else {
      const resData = await response.json();
      return resData.events;
   }
}

export async function loader({ request, params }) {
   const eventId = params.eventID;

   return defer({
      event: loadEvent(eventId),
      events: loadEvents(),
   });
}

export async function action({ params, request }) {
   const eventId = params.eventID;
   const response = await fetch('http://localhost:8080/events/' + eventId, { method: request.method });

   if (!response.ok) {
      throw json({ message: 'Cound not delete event.' }, { status: 500 });
   }

   return redirect('/events');
}
